class Solution:

    def longestValidSubstring(self, word, forbidden):
            print(word)
            if word == None:
                return 0, None
            elif word == "":
                return len(word), word
            fIndex, fWord, lIndex, lWord, new_forbbinden = self.findNextSubString_optimal(word, forbidden)
            print("fIndex", fIndex, fWord , word)
            print("lIndex", lIndex, lWord , word)
            if fWord == None and lWord == None:
                return len(word), word
            word1 = None
            word2 = None
            word3 = None
            word1 = word[:fIndex + len(fWord) - 1]
            if fIndex != lIndex:
                word2 = word[fIndex + 1:lIndex + len(lWord) - 1]
                word3 = word[lIndex + 1:]
            else:
                word2 = word[fIndex + 1:]
            print("split ", word, word1, word2, word3)
            subLen1, a1, = self.longestValidSubstring(word1, new_forbbinden)
            subLen2, a2 = self.longestValidSubstring(word2, new_forbbinden)
            subLen3, a3 = self.longestValidSubstring(word3, new_forbbinden)
            return max(subLen1, subLen2, subLen3), (a1, a2, a3)
        
    def findNextSubString_optimal(self, word, forbidden):
        print("findNextSubString_optimal", word)
        fIndex = None
        fWord = None
        lIndex = None
        lWord = None
        new_forbbinden = set()
        all_findings = dict()
        for fb1 in forbidden:
            if fb1 in word:
                if fIndex == None:
                    fIndex = word.index(fb1)
                    fWord = fb1
                    lIndex = word.index(fb1)
                    lWord = fb1
                elif fIndex > word.index(fb1):
                    fIndex = word.index(fb1)
                    fWord = fb1
                elif lIndex < word.index(fb1):
                    lIndex = word.index(fb1)
                    lWord = fb1
                new_forbbinden.add(fb1)
        print("len of forbidden", len(forbidden) - len(new_forbbinden))
        return fIndex, fWord, lIndex, lWord, new_forbbinden


