class Solution:

    def longestValidSubstring(self, word, forbidden):
            print(word)
            if word == "":
                return len(word), word
            earliestIndex, earliestFB,new_forbbinden = self.findNextSubString(word, forbidden)
            print("earliestIndex", earliestIndex, earliestFB , word)
            if earliestFB == None:
                return len(word), word
            word1 = word[:earliestIndex+len(earliestFB)-1]
            word2 = word[earliestIndex + 1:]
            print("split ", word, word1, word2)
            subLen1, a1, = self.longestValidSubstring(word1, new_forbbinden)
            subLen2, a2 = self.longestValidSubstring(word2, new_forbbinden)
            if subLen1 > subLen2:
                return subLen1, a1
            else:
                return subLen2, a2
        
    def findNextSubString(self, word, forbidden):
        print("findNextSubString", word)
        earliestIndex = None
        earliestFB = None
        new_forbbinden=set()
   
        for fb1 in forbidden:
            if fb1 in word:
                if earliestIndex == None:
                    earliestIndex = word.index(fb1)
                    earliestFB = fb1
                elif earliestIndex > word.index(fb1):
                    earliestIndex = word.index(fb1)
                    earliestFB = fb1
                new_forbbinden.add(fb1)
        print("len of forbidden",len(forbidden))
        return earliestIndex, earliestFB,new_forbbinden
    
    def findNextSubString_optimal(self, word, forbidden):
        print("findNextSubString", word)
        earliestIndex = None
        earliestFB = None
        new_forbbinden=set()
        all_findings=dict()
        for fb1 in forbidden:
            if fb1 in word:
                all_findings[word.index(fb1)]=fb1
                new_forbbinden.add(fb1)
        print("len of forbidden",len(forbidden))
        return earliestIndex, earliestFB,new_forbbinden


